function swipeUnidade(direction) {
    let scrollContainer = document.querySelector('.botoes-image-box');
    let scrollContainerWidth = scrollContainer.offsetWidth;
    let scroll = scrollContainerWidth / 2

    if (direction === "left") {
        scrollContainer.scrollBy({
            left: -scroll,
            behavior: "smooth"
        });
    }

    if (direction === "right") {
        scrollContainer.scrollBy({
            left: scroll,
            behavior: "smooth"
        });
    }
}


let elementoComScroll = document.querySelector('.botoes-image-box')
let setas = document.querySelectorAll('.unidade-swipper a')

elementoComScroll.addEventListener('scroll', function() {
    var scrollPosicao = elementoComScroll.scrollLeft;

    if (scrollPosicao === 0) {
        setas[1].style.opacity = "1";
        setas[0].style.opacity = "0.2";
    } else if (scrollPosicao + elementoComScroll.clientWidth >= elementoComScroll.scrollWidth) {
        setas[1].style.opacity = "0.2";
        setas[0].style.opacity = "1";
    } else {
        setas[0].style.opacity = "1";
        setas[1].style.opacity = "1";
    }
});
